insert into article(idArticle, Name, Picture, Description,Category_idCategory, AvailableQuantity,SalePrice,ProcurementPrice, isAvailable) values('1101', 'Ruža Nikolo Paganini','ruzaNikoloPaganini.jpg','Dekorativna i efektna, jedna od najljepših crvenih čajevki.', '1', '100', '4.00','2.00', '1');
insert into article(idArticle, Name, Picture, Description,Category_idCategory, AvailableQuantity,SalePrice,ProcurementPrice, isAvailable) values('1102', 'Ruža Tamora','ruzaTamora.jpg','Engleska ruza Tamora je ruža svijetle boje kajsije krupnih, kovrdžavih cvjetova izuzetnog mirisa. Ruža izuzetno velikih cvjetova prepuna brojnih latica. Cvjeta od maja do oktobra, zahtjeva puno sunca.', '1', '100', '5.80','1.00', '1');
insert into article(idArticle, Name, Picture, Description,Category_idCategory, AvailableQuantity,SalePrice,ProcurementPrice, isAvailable) values('1103', 'Ruža Renaisssance','ruzaRenaisssance.jpg','Šatirana, dvobojna ljepotica i mnogocvjetnica. Raste od 80-100cm i cvjeta dugo u više navrata. Prilično je otporna na bolesti ruža, a zbog specifičnog izgleda je nezaobilazna u vrtu.', '1', '100', '3.00','1.00', '1');
insert into article(idArticle, Name, Picture, Description,Category_idCategory, AvailableQuantity,SalePrice,ProcurementPrice, isAvailable) values('1104', 'Ruža Friesia','ruzaFriesia.jpg','Mnogocvjetnica čiste žute boje. Ruža ima čvrst i dug pupoljak, pa je dobar izbor i za rezani cvijet. Cvjeta od maja do oktobra i prilično je otporna na bolesti ruža.', '1', '100', '5.50','2.00', '1');
insert into article(idArticle, Name, Picture, Description,Category_idCategory, AvailableQuantity,SalePrice,ProcurementPrice, isAvailable) values('1201', 'Fotinija Fraseri Carre Rouge','ukrasnoZbunjeFotinijaFraseriCarreRouge.jpg','Ova vrsta Fotinije je zimzeleni žbun, koji ima manje listove od Red Robin vrste. Listovi su u osnovu tamno-zeleni, ali mladi listovi su izrazito crvene i upečatljive boje. Pojavljuju se dva puta godišnje: u rano proljeće i kasno ljeto. Fotinija koja ne raste visoko 2-2,5m i voli sunčane i polusunčane pozicije.', '2', '100', '13.50','5.00', '1');
insert into article(idArticle, Name, Picture, Description,Category_idCategory, AvailableQuantity,SalePrice,ProcurementPrice, isAvailable) values('1202', 'Kalistemon','ukrasnoZbunjeKalistemon.jpg','Cvijet Kalistemona jake crvene boje i oblika koji podsjeća na četku za flaše. Zbog izgleda cvijeta zovu ga i četkovac. U saksiji deluje vrlo dekorativan i atraktivan.', '2', '100', '8.50','3.00', '1');
insert into article(idArticle, Name, Picture, Description,Category_idCategory, AvailableQuantity,SalePrice,ProcurementPrice, isAvailable) values('1203', 'Oranz Berberis','ukrasnoZbunjeOranzBerberis.jpg','Ovo je prelijepa sorta zimzelenog Berberisa sa grozdovima narandžasto - žutog cvijeta koji u proljeće kao plamen okiti biljku.', '2', '100', '7.50','4.00', '1');
insert into article(idArticle, Name, Picture, Description,Category_idCategory, AvailableQuantity,SalePrice,ProcurementPrice, isAvailable) values('1204', 'Viburnum Davidii Zimzelena Udika','ukrasnoZbunjeViburnumDavidiiZimzelenaUdika.jpg','Pored sitnih krem-bijelih cvjetova, ova Udika ima izrazito upadljive, sjajne i duge zimzelene listove. Ova vrsta ne raste visoko 1,5-2m, isto tako i u širinu. Zato je pogodna za sadnju u većim grupama gdje postiže najbolji efekat.', '2', '100', '10.00','6.00', '1');
insert into article(idArticle, Name, Picture, Description,Category_idCategory, AvailableQuantity,SalePrice,ProcurementPrice, isAvailable) values('1301', 'Duglazija-Pseudotsuga menziesii','cetinariDuglazija.jpg','Nježne, meke četine daju joj specifičan izgled. Pored četina, krase je i šišarke duge do 10cm. Odlikuje je brzi rast i dobro podnošenje gradskih uslova.', '3', '100', '15.00','9.00', '1');
insert into article(idArticle, Name, Picture, Description,Category_idCategory, AvailableQuantity,SalePrice,ProcurementPrice, isAvailable) values('1302', 'Patuljasti bor-Pinus mugo','cetinariPatuljastiBorPinusMugo.jpg','Atraktivan i uvijek popularan ovaj patuljasti bor idealan je za male vrtove, žardinjere, krovne vrtove, velike terase, padine.', '3', '100', '10.00','6.00', '1');
insert into article(idArticle, Name, Picture, Description,Category_idCategory, AvailableQuantity,SalePrice,ProcurementPrice, isAvailable) values('1303', 'Jelka-Smrča','cetinariJelkaSmrca.jpg','Mirišljavi četinar najviše korišćen kao Novogodišnja jelka. Odlično podnosi orezivanje i može se formirati kao živa ograda ili gusta jelka za kićenje. Imajte pravu jelku u svom dvorištu.', '3', '100', '13.00','9.00', '1');
insert into article(idArticle, Name, Picture, Description,Category_idCategory, AvailableQuantity,SalePrice,ProcurementPrice, isAvailable) values('1304', 'Crni bor','cetinariCrniBor.jpg','Crni bor je graciozan četinar čija krošnja je kupasta do kišobranaste i zbog toga je jako interesantan. Ovaj bor uspijeva u jako oskudnim uslovima, što govori o njegovoj otpornosti. Ima izuzetno jak korijen, pa se koristi i za pošumljavanje nagiba i kosih terena. Šišarke kod bora se pojavljuju u kasnu jesen i na zimu i ostaju na granama kao dekoracija.', '3', '100', '16.00','10.00', '1');
insert into article(idArticle, Name, Picture, Description,Category_idCategory, AvailableQuantity,SalePrice,ProcurementPrice, isAvailable) values('1401', 'Spirea - zlatna princeza','zivaOgradaSpireaZlatnaPrinceza.jpg','Jedna od najljepših Spirea, kompaktna, gusta i listom vrlo upadljiva. Može se saditi pojedinačno ili u grupama i lejama kada posebno izgleda efektno.', '4', '100', '6.50','4.00', '1');
insert into article(idArticle, Name, Picture, Description,Category_idCategory, AvailableQuantity,SalePrice,ProcurementPrice, isAvailable) values('1402', 'Lejlandski čempres zeleni','zivaOgradaLejlandskiCempresZeleni.jpg','Otporan četinar brzog rasta koji se najviše koristi za formiranje gustih i visokih živih ograda. Živa ograda ne mora biti široka zbog mogućnosti orezivanja koju ova biljka dobro podnosi. Atraktivan i za pojedinačnu sadnju zbog mogućnosti oblikovanja u spiral ili drugi željeni oblik.', '4', '100', '7.00','4.00', '1');
insert into article(idArticle, Name, Picture, Description,Category_idCategory, AvailableQuantity,SalePrice,ProcurementPrice, isAvailable) values('1403', 'Cvetna lovor višnja','zivaOgradaCvetnaLovorVisnja.jpg','Za razliku od ostalih lovor višnji ova vrsta posebno lijepo i upadljivo cvjeta. Ova vrsta ima bele cvjetove koji cvjetaju u maju i junu. Vrlo izdržljiva i podnosi i zasjenčene terene.', '4', '100', '8.00','4.00', '1');
insert into article(idArticle, Name, Picture, Description,Category_idCategory, AvailableQuantity,SalePrice,ProcurementPrice, isAvailable)values('1404', 'Lovor višnja','zivaOgradaLovorVisnja.jpg','Ako vam treba jaka, široka i visoka ograda to je lovor višnja. Koristi se za šišane i slobodno rastuće živice. Ima više sorti.', '4', '100', '6.50','3.00', '1');
insert into article(idArticle, Name, Picture, Description,Category_idCategory, AvailableQuantity,SalePrice,ProcurementPrice, isAvailable)values('1501', 'Lale Lily Mix','lukoviceLaleLilyMix.jpg','Elegantne i raznobojne lale, sa specifičnim špicastim laticama ukrasiće Vaše ivice travnjaka, staza i kamenjara, u grupama kako su najljepše. Zajedno sa jednobojnim i lukovicama nižeg rasta dobićete potpunu kompoziciju.', '5', '100', '6.00','4.00', '1');
insert into article(idArticle, Name, Picture, Description,Category_idCategory, AvailableQuantity,SalePrice,ProcurementPrice, isAvailable)values('1502', 'Ljiljan narandžasti Orange County','lukoviceJanNarandzastiOrangeCounty.jpg','Kada pomislite na biljku ljiljan, pomislite na narandžasti ljiljan, baš ovaj! Fenomenalna boja za sve smjele baštovane, ukrasiće Vaše bašte i pokupiti sve poglede, a ako poželite da ga zadržite samo za sebe ljiljani su odlični za rezani cvijet.', '5', '100', '8.50','5.00', '1');
insert into article(idArticle, Name, Picture, Description,Category_idCategory, AvailableQuantity,SalePrice,ProcurementPrice, isAvailable)values('1503', 'Lala Fringed Bresta','lukoviceLalaFringedBresta.jpg','Lala čiji cvijet izgleda kao da je posut injem. Poslije snijega i zime, obradovaće vas boje ove specifične i svakako nesvakidašnje lale. Pogodna je za sadnju u saksijama i vazama, kao i u vašim vrtovima.', '5', '100', '7.50','5.00', '1');
insert into article(idArticle, Name, Picture, Description,Category_idCategory, AvailableQuantity,SalePrice,ProcurementPrice, isAvailable)values('1504', 'Narcis dupli Rip Van Winkle','lukoviceNarcisDupliRipVanWinkle.jpg','Patuljasti narcis koji ima neobične latice, zelenkasto žute boje koji izgledaju vrlo različito od drugih narcisa. Njegova kraća visina čini ga idealnim za sadnju na prednjoj strani. Lijepo bi se uklopio sa lalama jačih boja.', '5', '100', '7.00','5.00', '1');
insert into article(idArticle, Name, Picture, Description,Category_idCategory, AvailableQuantity,SalePrice,ProcurementPrice, isAvailable)values('1601', 'Makaze za orezivanje KEW','alatiMakazeZaOrezivanjeKew.jpg','Makaze sa sječivima od visokog ugljeničnog čelika za snagu i trajnu oštrinu sa podesivim zatezanjem.', '6', '50', '50.00','35.00', '1');
